import React, { Component } from "react";
import {
  Grid,
  Cell,
  Card,
  CardTitle,
  CardText,
  CardMenu,
  IconButton,
} from "react-mdl";

class aboutme extends Component {
  toggleCategories() {
    return (
      <div>
        <section className="projects-grid">
          {/* Project 1 */}
          <Card shadow={5} style={{ minWidth: "450", margin: "auto" }}>
            <CardTitle
              style={{
                color: "#fff",
                height: "176px",
                background:
                  "url(https://cdn2.iconfinder.com/data/icons/profession-avatar-2/128/avatar-marine-soldier-army-512.png) center / cover",
              }}
            ></CardTitle>
            <CardText>
              <h5
                style={{
                  color: "#CD853F",
                  fontFamily: "Arial Black",
                  fontweight: "bold",
                }}
              >
                {" "}
                Geek{" "}
              </h5>
              My favorite field is programming. I like it because everything is
              so logical and with the proper language we can carry out amazing
              projects.
            </CardText>

            <CardMenu style={{ color: "#fff" }}>
              <IconButton name="share" />
            </CardMenu>
          </Card>

          {/* Project 2 */}

          {/* Project 3 */}
          <Card shadow={5} style={{ minWidth: "450", margin: "auto" }}>
            <CardTitle
              style={{
                color: "#fff",
                height: "176px",
                background:
                  "url(https://cdn0.iconfinder.com/data/icons/office-culture/32/11_office_team_people_avatar_profession-512.png) center / cover",
              }}
            ></CardTitle>
            <CardText>
              <h5
                style={{
                  color: "#FF1493",
                  fontFamily: "Arial Black",
                  fontweight: "bold",
                }}
              >
                {" "}
                Team worker{" "}
              </h5>
              There’s a synergy in teamwork that can accomplish far more than
              the same individuals working alone. When people work together,
              there’s nothing like it. I’ve always worked well with others, but
              I have no difficulty working independently.
            </CardText>

            <CardMenu style={{ color: "#fff" }}>
              <IconButton name="share" />
            </CardMenu>
          </Card>
        </section>
        <br />
        <section className="projects-grid">
          <Card shadow={5} style={{ minWidth: "450", margin: "auto" }}>
            <CardTitle
              style={{
                color: "#fff",
                height: "176px",
                background:
                  "url(https://cdn4.vectorstock.com/i/1000x1000/95/98/man-practicing-swimming-avatar-character-vector-22329598.jpg) center / cover",
              }}
            ></CardTitle>
            <CardText>
              <h5
                style={{
                  color: "#00CED1",
                  fontFamily: "Arial Black",
                  fontweight: "bold",
                }}
              >
                {" "}
                Sports{" "}
              </h5>
              Sport has the power to overcome old division and create the bond
              of common aspiration.
            </CardText>

            <CardMenu style={{ color: "#fff" }}>
              <IconButton name="share" />
            </CardMenu>
          </Card>
        </section>
        <section className="projects-grid">
          {/* Project 3 */}
          <Card shadow={5} style={{ minWidth: "450", margin: "auto" }}>
            <CardTitle
              style={{
                color: "#fff",
                height: "176px",
                background:
                  "url(https://cdn1.iconfinder.com/data/icons/human-profession/1000/Farmer-2-512.png) center / cover",
              }}
            ></CardTitle>
            <CardText>
              <h5
                style={{
                  color: "#3CB371",
                  fontFamily: "Arial Black",
                  fontweight: "bold",
                }}
              >
                {" "}
                Go green{" "}
              </h5>
              All I want for tomorrow is a healthy planet. Go green we don't
              have another planet.
            </CardText>

            <CardMenu style={{ color: "#fff" }}>
              <IconButton name="share" />
            </CardMenu>
          </Card>
          {/* Project 3 */}

          {/* Project 3 */}
          <Card shadow={5} style={{ minWidth: "450", margin: "auto" }}>
            <CardTitle
              style={{
                color: "#fff",
                height: "176px",
                background:
                  "url(https://cdn.dribbble.com/users/500242/screenshots/3139710/72h-dribbble.gif) center / cover",
              }}
            ></CardTitle>
            <CardText>
              <h5
                style={{
                  color: "#FF7F50",
                  fontFamily: "Arial Black",
                  fontweight: "bold",
                }}
              >
                {" "}
                Travel
              </h5>
              As much as you can, as far as you can, as long as you can. Life's
              not meant to be lived in one place.
            </CardText>

            <CardMenu style={{ color: "#fff" }}>
              <IconButton name="share" />
            </CardMenu>
          </Card>
        </section>
      </div>
    );
  }

  render() {
    return (
      <div>
        <Grid>
          <Cell col={12}>
            <div className="content">{this.toggleCategories()}</div>
          </Cell>
        </Grid>
      </div>
    );
  }
}

export default aboutme;
